export const getRandomPets = (count: number = 10): Pet[] => {
  const selectedPets: Pet[] = [];
  const availablePets = [...pets];

  // Ensure at least 1 Rare or higher rarity pet
  const rareAndHigher = availablePets.filter(pet => 
    ['Rare', 'Legendary', 'Mythical', 'Divine', 'Prismatic'].includes(pet.rarity)
  );

  if (rareAndHigher.length > 0) {
    const guaranteedRare = rareAndHigher[Math.floor(Math.random() * rareAndHigher.length)];
    selectedPets.push(guaranteedRare);
    availablePets.splice(availablePets.indexOf(guaranteedRare), 1);
  }

  // Fill remaining slots with random pets based on probability
  for (let i = selectedPets.length; i < count; i++) {
    const random = Math.random();
    let cumulativeProbability = 0;
    let selectedPet: Pet | null = null;

    for (const pet of availablePets) {
      cumulativeProbability += pet.probability;
      if (random <= cumulativeProbability) {
        selectedPet = pet;
        break;
      }
    }

    if (selectedPet) {
      selectedPets.push(selectedPet);
      availablePets.splice(availablePets.indexOf(selectedPet), 1);
    } else {
      // Fallback to a random pet if probability calculation fails
      const randomIndex = Math.floor(Math.random() * availablePets.length);
      selectedPets.push(availablePets[randomIndex]);
      availablePets.splice(randomIndex, 1);
    }
  }

  return selectedPets;
};
