import React, { useState } from 'react';
import { RotateCcw, Gift, Zap } from 'lucide-react';
import { useSpinner } from '../contexts/SpinnerContext';
import { useAuth } from '../contexts/AuthContext';
import { lineNotificationService } from '../services/lineNotification';
import toast from 'react-hot-toast';

const Spinner: React.FC = () => {
  const { isSpinning, currentItem, spin, resetSpinner } = useSpinner();
  const { user } = useAuth();
  const [showResult, setShowResult] = useState(false);

  const handleSpin = async () => {
    if (!user) {
      toast.error('Please login to use the spinner!');
      return;
    }

    setShowResult(false);
    const wonItem = await spin();
    setShowResult(true);

    // Send Line notification
    try {
      await lineNotificationService.sendSpinnerWinNotification(
        user.name,
        wonItem.name,
        wonItem.rarity
      );
    } catch (error) {
      console.error('Failed to send notification:', error);
    }

    toast.success(`Congratulations! You won ${wonItem.name}!`);
  };

  const getRarityColor = (rarity: string) => {
    switch (rarity) {
      case 'common': return 'text-gray-600';
      case 'rare': return 'text-blue-600';
      case 'epic': return 'text-purple-600';
      case 'legendary': return 'text-yellow-600';
      default: return 'text-gray-600';
    }
  };

  const getRarityBg = (rarity: string) => {
    switch (rarity) {
      case 'common': return 'bg-gray-100';
      case 'rare': return 'bg-blue-100';
      case 'epic': return 'bg-purple-100';
      case 'legendary': return 'bg-yellow-100';
      default: return 'bg-gray-100';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-blue-50 py-12">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            üé∞ Lucky Spin
          </h1>
          <p className="text-lg text-gray-600">
            Try your luck and win amazing items!
          </p>
        </div>

        <div className="grid md:grid-cols-2 gap-8">
          {/* Spinner Section */}
          <div className="bg-white rounded-lg shadow-lg p-8">
            <div className="text-center">
              <div className="relative mb-8">
                {/* Spinner Wheel */}
                <div className={`w-64 h-64 mx-auto rounded-full border-8 border-gray-300 relative overflow-hidden ${
                  isSpinning ? 'animate-spin-slow' : ''
                }`}>
                  <div className="absolute inset-0 bg-gradient-to-r from-red-500 via-yellow-500 to-green-500 opacity-20"></div>
                  <div className="absolute inset-2 bg-white rounded-full flex items-center justify-center">
                    {isSpinning ? (
                      <div className="text-center">
                        <Zap className="w-12 h-12 text-yellow-500 mx-auto animate-bounce" />
                        <p className="text-sm text-gray-600 mt-2">Spinning...</p>
                      </div>
                    ) : (
                      <div className="text-center">
                        <Gift className="w-12 h-12 text-primary-600 mx-auto" />
                        <p className="text-sm text-gray-600 mt-2">Click to spin!</p>
                      </div>
                    )}
                  </div>
                </div>

                {/* Spinner Pointer */}
                <div className="absolute top-0 left-1/2 transform -translate-x-1/2 -translate-y-2">
                  <div className="w-0 h-0 border-l-8 border-r-8 border-b-12 border-l-transparent border-r-transparent border-b-red-500"></div>
                </div>
              </div>

              <button
                onClick={handleSpin}
                disabled={isSpinning || !user}
                className="bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700 text-white font-bold py-3 px-8 rounded-full text-lg shadow-lg transform hover:scale-105 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
              >
                {isSpinning ? 'Spinning...' : 'SPIN NOW!'}
              </button>

              {!user && (
                <p className="text-red-600 text-sm mt-4">
                  Please login to use the spinner
                </p>
              )}
            </div>
          </div>

          {/* Result Section */}
          <div className="bg-white rounded-lg shadow-lg p-8">
            <h2 className="text-2xl font-bold text-gray-900 mb-6 text-center">
              Your Prize
            </h2>

            {showResult && currentItem ? (
              <div className="text-center">
                <div className={`w-32 h-32 mx-auto rounded-full ${getRarityBg(currentItem.rarity)} flex items-center justify-center mb-4`}>
                  <span className="text-6xl">{currentItem.image}</span>
                </div>

                <h3 className={`text-xl font-bold mb-2 ${getRarityColor(currentItem.rarity)}`}>
                  {currentItem.name}
                </h3>

                <span className={`inline-block px-3 py-1 rounded-full text-sm font-medium ${getRarityBg(currentItem.rarity)} ${getRarityColor(currentItem.rarity)}`}>
                  {currentItem.rarity.toUpperCase()}
                </span>

                <div className="mt-6">
                  <button
                    onClick={resetSpinner}
                    className="flex items-center space-x-2 mx-auto bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-md transition-colors"
                  >
                    <RotateCcw size={16} />
                    <span>Spin Again</span>
                  </button>
                </div>
              </div>
            ) : (
              <div className="text-center text-gray-500">
                <Gift className="w-16 h-16 mx-auto mb-4 opacity-50" />
                <p>Spin the wheel to see what you win!</p>
              </div>
            )}
          </div>
        </div>

        {/* Rarity Guide */}
        <div className="mt-12 bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-xl font-bold text-gray-900 mb-4 text-center">
            Item Rarities
          </h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="text-center">
              <div className="w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-2">
                <span className="text-2xl">‚öîÔ∏è</span>
              </div>
              <p className="font-medium text-gray-600">Common</p>
              <p className="text-sm text-gray-500">60% chance</p>
            </div>
            <div className="text-center">
              <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-2">
                <span className="text-2xl">üîÆ</span>
              </div>
              <p className="font-medium text-blue-600">Rare</p>
              <p className="text-sm text-gray-500">25% chance</p>
